CREATE TYPE odos_crrs_svc.equipm_report_status AS ENUM (
	'ACCEPTED', 'REPORTED', 'DENIED', 'RESOLVED');

	create table odos_crrs_svc.equip_report (
	id serial not null,
	description varchar(250) not null,
	resolution varchar(250),
	flag odos_crrs_svc.equipm_report_status,
	conf_rm_equip_id serial not null references odos_crrs_svc.conf_rm_equip (conf_rm_equip_id),

	primary key (id)
	);
